// screens/HomeScreen.js
import React, { useState, useEffect } from "react";
import { View, Text, FlatList, TouchableOpacity, Image, StyleSheet } from "react-native";
import Icon from "react-native-vector-icons/FontAwesome";

const HomeScreen = ({ navigation }) => {
  const [menuItems, setMenuItems] = useState([]);
  const [loading, setLoading] = useState(true);

  
  const mockMenuItems = [
    {
      id: "1",
      title: "Burger",
      price: 5.99,
      image: "data:image/jpeg;base64,",
      description:"A burger is a dish consisting of a cooked, ground-meat patty, typically beef, served inside a sliced bread roll or bun with various toppings like lettuce, tomato, cheese, and condiments such as ketchup and mustard."
    },
    {
      id: "2",
      title: "Pizza",
      price: 8.99,
      image: "data:image/jpeg;base64,",
      description: "Pizza is a popular baked dish of Italian origin that consists of a flattened, leavened dough base topped with ingredients like tomato sauce, cheese, and various meats or vegetables."
    },
    {
      id: "3",
      title: "Pasta",
      price: 6.99,
      image: "data:image/jpeg;base64,",
      description: "Pasta is a versatile staple food, originating in Italian cuisine, made from an unleavened dough of durum wheat flour (semolina), water, and sometimes eggs, which is formed into various shapes like sheets, tubes, or ribbons before being cooked by boiling or baking"
    },
  ];

  useEffect(() => {
    setTimeout(() => {
      setMenuItems(mockMenuItems);
      setLoading(false);
    }, 1000);
  }, []);

  const handleLogout = () => {
    navigation.navigate("Login");
  };

  const renderItem = ({ item }) => (
    <TouchableOpacity
      style={styles.itemContainer}
      onPress={() => navigation.navigate("ItemDetail", { item })}
    >
      <Image source={{ uri: item.image }} style={styles.itemImage} />
      <View style={styles.itemInfo}>
        <Text style={styles.itemTitle}>{item.title}</Text>
        <Text style={styles.itemPrice}>${item.price}</Text>
      </View>
    </TouchableOpacity>
  );

  return (
    <View style={styles.container}>
      <View style={styles.header}>
        <Text style={styles.headerText}>Menu</Text>
        <TouchableOpacity onPress={handleLogout}>
          <Icon name="sign-out" size={24} color="#3498db" />
        </TouchableOpacity>
      </View>

      {loading ? (
        <Text style={{ textAlign: "center", marginTop: 20 }}>Loading...</Text>
      ) : (
        <FlatList
          data={menuItems}
          renderItem={renderItem}
          keyExtractor={(item) => item.id}
          contentContainerStyle={styles.list}
        />
      )}
    </View>
  );
};
export default HomeScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
  header: {
    flexDirection: "row",
    justifyContent: "space-between",
    alignItems: "center",
    padding: 15,
    borderBottomWidth: 1,
    borderBottomColor: "#eee",
  },
  headerText: {
    fontSize: 20,
    fontWeight: "bold",
  },
  list: {
    padding: 15,
  },
  itemContainer: {
    flexDirection: "row",
    marginBottom: 15,
    backgroundColor: "#f9f9f9",
    borderRadius: 8,
    overflow: "hidden",
  },
  itemImage: {
    width: 100,
    height: 100,
  },
  itemInfo: {
    flex: 1,
    padding: 15,
    justifyContent: "center",
  },
  itemTitle: {
    fontSize: 16,
    fontWeight: "bold",
    marginBottom: 5,
  },
  itemPrice: {
    fontSize: 14,
    color: "#888",
  },
});


